import React, { Component } from 'react'
import ApiLogin from "../../ApiConfig"
import PropTypes from 'prop-types'

import './tweet.css'
import {TweetsServices} from "../../services/TweetsService"

class Tweet extends Component {

    static propTypes = {
        removivel: PropTypes.bool,
        totalLikes: PropTypes.number.isRequired,
        likeado: PropTypes.bool.isRequired,
        id:PropTypes.string.isRequired,
        usuario: PropTypes.shape({
            foto:PropTypes.string,
            login: PropTypes.string,
            nome: PropTypes.string
        }),
        conteudo: PropTypes.string,
        onRemove: PropTypes.func,
        onClickNaAreaDeConteudo: PropTypes.func
    }

    static defaultProps = {
        usuario: {},
        likeado: false,
    }

    constructor(props){
        super(props)

        this.state = {
            likeado: props.likeado,
            totalLikes: props.totalLikes
        }

    }


    handleClickNaAreaDeConteudo= () =>{
        this.props.onClickNaAreaDeConteudo && this.props.onClickNaAreaDeConteudo()
    }

    likeHandler = () => {
        const {likeado, totalLikes} = this.state
        const idDoTweet = this.props.id

        this.setState({
            likeado:!likeado,
            totalLikes: likeado ? totalLikes - 1 : totalLikes + 1
        })

        TweetsServices.like(idDoTweet).then(response => console.log(response))

    }

    render() {

        return (
            <article className="tweet">
                <div className="tweet__cabecalho">
                    <img className="tweet__fotoUsuario" src={this.props.usuario.foto} alt="" />
                    <span className="tweet__nomeUsuario">{this.props.usuario.nome}</span>
                    <a href="/"><span className="tweet__userName">@{this.props.usuario.login}</span></a>
                </div>
                <p className="tweet__conteudo" onClick={this.handleClickNaAreaDeConteudo}>
                    {this.props.texto}
                </p>
                <footer className="tweet__footer">
                    <button onClick={this.likeHandler} className="btn btn--clean">
                        <svg className={`icon icon--small iconHeart ${this.state.likeado ? 'iconHeart--active' : ""}`} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 47.5 47.5">
                            <defs>
                                <clipPath id="a">
                                    <path d="M0 38h38V0H0v38z"></path>
                                </clipPath>
                            </defs>
                            <g clipPath="url(#a)" transform="matrix(1.25 0 0 -1.25 0 47.5)">
                                <path d="M36.885 25.166c0 5.45-4.418 9.868-9.867 9.868-3.308 0-6.227-1.632-8.018-4.128-1.79 2.496-4.71 4.129-8.017 4.129-5.45 0-9.868-4.418-9.868-9.868 0-.773.098-1.52.266-2.242C2.75 14.413 12.216 5.431 19 2.965c6.783 2.466 16.249 11.448 17.617 19.96.17.721.268 1.47.268 2.241"></path>
                            </g>
                        </svg>
                        {this.state.totalLikes}
                    </button>
                    {
                        this.props.removivel && 
                        <button onClick={this.props.removeHandler} className="btn btn--blue btn--remove"></button>
                    }
                </footer>
            </article>
        )
    }
}



export default Tweet
